import React from 'react';
import styled from 'styled-components';
import { AppColors } from '../../styles/colors';
import { AppTextStyles } from '../../styles/textStyles';
import { ScheduleEvent, StaffInfo } from './types';
import { getMonthDates, isEventOnDate, isSameMonth } from './utils';

interface MonthViewProps {
  currentDate: Date;
  events: ScheduleEvent[];
  staffList: StaffInfo[];
  selectedStaffIds: string[];
  onEventClick?: (event: ScheduleEvent) => void;
  onEventCreate?: (startTime: Date, endTime: Date, staffId?: string) => void;
  onDateClick?: (date: Date) => void;
}

const MonthContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  border: 1px solid ${AppColors.borderLight};
  border-radius: 8px;
  background-color: ${AppColors.surface};
  overflow: hidden;
`;

const Header = styled.div`
  display: flex;
  background-color: ${AppColors.background};
  border-bottom: 1px solid ${AppColors.borderLight};
`;

const DayHeader = styled.div`
  flex: 1;
  padding: 12px 8px;
  text-align: center;
  border-right: 1px solid ${AppColors.borderLight};
  font-size: ${AppTextStyles.body2.fontSize};
  font-weight: 600;
  color: ${AppColors.onSurface};

  &:last-child {
    border-right: none;
  }
`;

const WeekContainer = styled.div`
  display: flex;
  flex: 1;
  min-height: 120px;
  border-bottom: 1px solid ${AppColors.borderLight};

  &:last-child {
    border-bottom: none;
  }
`;

const DayCell = styled.div<{ 
  isToday: boolean; 
  isCurrentMonth: boolean; 
  isWeekend: boolean; 
  hasEvents: boolean;
}>`
  flex: 1;
  border-right: 1px solid ${AppColors.borderLight};
  padding: 8px;
  cursor: pointer;
  position: relative;
