      onEventCreate(startTime, endTime);
    }
  };

  const toggleStaffFilter = (staffId: string) => {
    // ì´ ê¸°ëŠ¥ì€ ìƒìœ„ ì»´í¬ë„ŒíŠ¸ì—ì„œ ì²˜ë¦¬í•´ì•¼ í•˜ë¯€ë¡œ ì½œë°±ì´ í•„ìš”í•˜ì§€ë§Œ
    // ì—¬ê¸°ì„œëŠ” ë‹¨ìˆœíˆ í‘œì‹œë§Œ í•©ë‹ˆë‹¤
  };

  if (filteredStaff.length === 0) {
    return (
      <MonthContainer>
        <div style={{ padding: '48px', textAlign: 'center', color: AppColors.onSurface + '60' }}>
          í‘œì‹œí•  ì½”ì¹˜ë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.
