/**
 * 달력 컴포넌트 관련 타입 정의
 */

export type CalendarView = 'day' | 'week' | 'month';

export interface ScheduleEvent {
  id: string;
  title: string;
  startTime: Date;
  endTime: Date;
  staffId: string;
  staffName: string;
  programId?: string;
  programName?: string;
  memberId?: string;
  memberName?: string;
  type: 'class' | 'personal' | 'meeting' | 'break';
  color?: string;
  description?: string;
}

export interface StaffInfo {
  id: string;
  name: string;
  role: string;
  program?: string;
  isActive: boolean;
  color?: string; // 달력에서 구분용 색상
}

export interface CalendarProps {
  view: CalendarView;
  currentDate: Date;
  events: ScheduleEvent[];
  staffList: StaffInfo[];
  selectedStaffIds: string[];
  onViewChange: (view: CalendarView) => void;
  onDateChange: (date: Date) => void;
  onStaffFilter: (staffIds: string[]) => void;
  onEventClick?: (event: ScheduleEvent) => void;
  onEventCreate?: (startTime: Date, endTime: Date, staffId?: string) => void;
}

export interface TimeSlot {
  hour: number;
  minute: number;
  display: string;
}

export interface DayColumn {
  date: Date;
  dayName: string;
  isToday: boolean;
  isWeekend: boolean;
}
