import React, { useRef } from 'react';
import styled from 'styled-components';
import { AppColors } from '../styles/colors';
import { AppTextStyles } from '../styles/textStyles';

interface CustomDateInputProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  disabled?: boolean;
  error?: boolean;
  required?: boolean;
}

const DateInputContainer = styled.div`
  position: relative;
  width: 100%;
`;

const DateInputWrapper = styled.div<{ $error?: boolean; $hasValue: boolean }>`
  height: 48px;
  padding: 0 16px;
  border: 1px solid ${props => props.$error ? AppColors.error : AppColors.borderLight};
  border-radius: 12px;
  background: ${AppColors.surface};
  color: ${AppColors.onSurface};
  transition: all 0.2s ease;
  box-sizing: border-box;
  display: flex;
  align-items: center;
  justify-content: space-between;
  cursor: pointer;

  &:hover {
    border-color: ${props => props.$error ? AppColors.error : AppColors.primary};
    box-shadow: 0 2px 8px rgba(55, 187, 214, 0.1);
  }

  &:focus-within {
    border-color: ${props => props.$error ? AppColors.error : AppColors.primary};
    box-shadow: 0 0 0 3px rgba(55, 187, 214, 0.1);
  }
`;

const HiddenDateInput = styled.input.attrs({ type: 'date' })`
  position: absolute;
  opacity: 0;
  pointer-events: none;
  width: 100%;
  height: 100%;
`;

const DateDisplay = styled.span<{ $hasValue: boolean }>`
  font-size: ${AppTextStyles.body1.fontSize};
  color: ${props => props.$hasValue ? AppColors.onSurface : AppColors.onInput1};
  flex: 1;
`;

const CalendarIcon = styled.div`
  width: 20px;
  height: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${AppColors.onInput1};
  margin-left: 8px;
`;

const CustomDateInput: React.FC<CustomDateInputProps> = ({
  value,
  onChange,
  placeholder = "ë‚ ì§œë¥¼ ì„ íƒí•˜ì„¸ìš”",
  disabled = false,
  error = false,
  required = false
}) => {
  const hiddenInputRef = useRef<HTMLInputElement>(null);

  const formatDateForDisplay = (dateValue: string): string => {
    if (!dateValue) return '';

    try {
      const date = new Date(dateValue);
      return date.toLocaleDateString('ko-KR', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch {
      return dateValue;
    }
  };

  const handleWrapperClick = () => {
    if (!disabled && hiddenInputRef.current) {
      if (hiddenInputRef.current.showPicker) {
        hiddenInputRef.current.showPicker();
      } else {
        hiddenInputRef.current.focus();
      }
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    onChange(e.target.value);
  };

  return (
    <DateInputContainer>
      <DateInputWrapper
        $error={error}
        $hasValue={!!value}
        onClick={handleWrapperClick}
      >
        <DateDisplay $hasValue={!!value}>
          {value ? formatDateForDisplay(value) : placeholder}
        </DateDisplay>
        <CalendarIcon>
          ðŸ“…
        </CalendarIcon>
      </DateInputWrapper>

      <HiddenDateInput
        ref={hiddenInputRef}
        value={value}
        onChange={handleInputChange}
        disabled={disabled}
        required={required}
      />
    </DateInputContainer>
  );
};

export default CustomDateInput;
