  background-color: ${props => props.selected ? AppColors.btnCEmphasis : AppColors.surface};
  transition: background-color 0.2s;

  &:hover {
    background-color: ${props => props.selected ? AppColors.btnCEmphasis : AppColors.background};
  }

  &:last-child {
    border-bottom: none;
  }
`;

const MemberInfo = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
`;

const MemberDetails = styled.div`
  flex: 1;
`;

const MemberName = styled.div`
  font-size: ${AppTextStyles.body1.fontSize};
  font-weight: 600;
  color: ${AppColors.onSurface};
  margin-bottom: 4px;
`;

const MemberPhone = styled.div`
  font-size: ${AppTextStyles.body2.fontSize};
  color: ${AppColors.onSurface}80;
  margin-bottom: 4px;
`;

const CourseInfo = styled.div`
  font-size: ${AppTextStyles.body2.fontSize};
