    name?: string;
  };
  existingEvents: ScheduleEvent[];
  onReservationCreate: (reservation: ScheduleEvent) => void;
}

interface MemberWithCourse extends Member {
  courseEnrollment?: CourseEnrollment;
  availableSessions?: number;
}

const ReservationModal: React.FC<ReservationModalProps> = ({
  isOpen,
  onClose,
  startTime,
  endTime,
  staffId,
  staffName,
  programId,
  programName,
  program,
  branchId,
  branchName,
  currentUser,
  existingEvents,
  onReservationCreate
}) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [availableMembers, setAvailableMembers] = useState<MemberWithCourse[]>([]);
  const [selectedMember, setSelectedMember] = useState<MemberWithCourse | null>(null);
  const [memo, setMemo] = useState('');
  const [loading, setLoading] = useState(false);
  const [timeConflict, setTimeConflict] = useState<string>('');

  // 권한 체크
  const hasPermission = useMemo(() => {
    if (!currentUser) return false;

    // 마스터는 모든 권한
    if (currentUser.role === 'master') return true;

    // 담당 코치만 해당 코치의 스케줄에 예약 가능
    if (currentUser.role === 'coach') {
      return currentUser.id === staffId;
    }

    return false;
  }, [currentUser, staffId]);

  // 시간 포맷팅
  const formatTime = (date: Date): string => {
    return date.toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: false
    });
  };

  const formatDate = (date: Date): string => {
    return date.toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      weekday: 'long'
    });
  };

  // 시간 겹침 체크
  const checkTimeConflict = useMemo(() => {
    const conflictingEvents = existingEvents.filter(event => {
      // 같은 스태프의 이벤트만 체크
      if (event.staffId !== staffId) return false;

      // 시간 겹침 체크
      const eventStart = new Date(event.startTime);
      const eventEnd = new Date(event.endTime);

      return (
        (startTime >= eventStart && startTime < eventEnd) ||
        (endTime > eventStart && endTime <= eventEnd) ||
        (startTime <= eventStart && endTime >= eventEnd)
      );
    });

    if (conflictingEvents.length > 0) {
      const conflictInfo = conflictingEvents.map(event => 
        `${formatTime(new Date(event.startTime))} - ${formatTime(new Date(event.endTime))} (${event.title})`
      ).join(', ');
      return `선택한 시간대에 다른 일정이 있습니다: ${conflictInfo}`;
    }

    return '';
  }, [existingEvents, staffId, startTime, endTime]);

  // 예약 가능한 회원 로드
  const loadAvailableMembers = useCallback(async () => {
    if (!isOpen) return;

    setLoading(true);
    try {
      // 해당 지점의 활성 회원 조회
      const allMembers = await dbManager.getAllMembers();
      const branchMembers = allMembers.filter(member => 
        member.branchId === branchId && 
        member.isActive &&
        (searchQuery === '' || member.name.toLowerCase().includes(searchQuery.toLowerCase()))
      );

      // 횟수제 수강권을 가진 회원들만 필터링
      const allEnrollments = await dbManager.getAllCourseEnrollments();
      const membersWithCourse: MemberWithCourse[] = [];

      for (const member of branchMembers) {
        // 해당 프로그램의 활성 횟수제 수강권 찾기
        const courseEnrollment = allEnrollments.find(enrollment =>
          enrollment.memberId === member.id &&
          enrollment.programId === programId &&
          enrollment.programType === '횟수제' &&
          enrollment.enrollmentStatus === 'active' &&
          (enrollment.completedSessions || 0) < (enrollment.sessionCount || 0)
        );

        if (courseEnrollment) {
          const availableSessions = (courseEnrollment.sessionCount || 0) - (courseEnrollment.completedSessions || 0);
          membersWithCourse.push({
            ...member,
            courseEnrollment,
            availableSessions
          });
        }
      }

      setAvailableMembers(membersWithCourse);
    } catch (error) {
      console.error('회원 목록 로드 실패:', error);
    } finally {
      setLoading(false);
    }
  }, [isOpen, branchId, programId, searchQuery]);

  // 예약 생성
  const handleCreateReservation = async () => {
    if (!selectedMember || !hasPermission) return;

    // 시간 겹침 체크
    if (timeConflict) {
      alert('시간이 겹치는 다른 일정이 있어 예약할 수 없습니다.');
      return;
    }

    setLoading(true);
    try {
      // 새 예약 이벤트 생성 (데이터베이스용 타입으로)
      const newReservationForDB: Omit<import('../utils/db/types').ScheduleEvent, 'id' | 'createdAt' | 'updatedAt'> = {
        title: `${selectedMember.name} (${programName})`,
        startTime,
        endTime,
        staffId,
        staffName,
        programId,
        programName,
        memberId: selectedMember.id,
        memberName: selectedMember.name,
        type: 'class',
        color: '#3b82f6', // 파란색으로 수업 표시
        description: memo || `${selectedMember.name} 회원의 ${programName} 수업`,
        branchId,
        branchName,
        sourceType: 'booking',
        sourceId: selectedMember.id
      };

      // 예약 저장
      const savedEvents = await dbManager.saveScheduleEvents([newReservationForDB]);
      const savedDBEvent = savedEvents[0];

      // Calendar 컴포넌트용 이벤트 객체로 변환
      const calendarEvent: ScheduleEvent = {
        id: savedDBEvent.id,
        title: savedDBEvent.title,
        startTime: savedDBEvent.startTime,
        endTime: savedDBEvent.endTime,
        staffId: savedDBEvent.staffId,
        staffName: savedDBEvent.staffName,
        programId: savedDBEvent.programId,
        programName: savedDBEvent.programName,
        memberId: savedDBEvent.memberId,
        memberName: savedDBEvent.memberName,
        type: savedDBEvent.type,
        color: savedDBEvent.color,
        description: savedDBEvent.description,
        branchId: savedDBEvent.branchId,
        branchName: savedDBEvent.branchName,
        sourceType: savedDBEvent.sourceType,
        sourceId: savedDBEvent.sourceId,
        createdAt: savedDBEvent.createdAt,
        updatedAt: savedDBEvent.updatedAt
      };

      // 수강권 세션 수 업데이트
      if (selectedMember.courseEnrollment) {
        const updatedEnrollment = {
          ...selectedMember.courseEnrollment,
          completedSessions: (selectedMember.courseEnrollment.completedSessions || 0) + 1,
          updatedAt: new Date()
        };

        await dbManager.updateCourseEnrollment(updatedEnrollment.id, updatedEnrollment);
      }

      // 회원 메모 업데이트 (메모가 있는 경우)
      if (memo.trim()) {
        const updatedMember = {
          ...selectedMember,
          remarks: selectedMember.remarks ? 
            `${selectedMember.remarks}\n\n[${new Date().toLocaleDateString('ko-KR')}] ${memo}` : 
            `[${new Date().toLocaleDateString('ko-KR')}] ${memo}`,
          updatedAt: new Date()
        };

        await dbManager.updateMember(updatedMember.id, updatedMember);
      }

      // 부모 컴포넌트에 새 예약 알림
      onReservationCreate(calendarEvent);

      alert('예약이 성공적으로 등록되었습니다.');
      handleClose();
    } catch (error) {
      console.error('예약 생성 실패:', error);
      alert('예약 등록 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  // 모달 닫기
  const handleClose = () => {
    setSearchQuery('');
    setSelectedMember(null);
    setMemo('');
    setTimeConflict('');
    onClose();
  };

  // 검색어 변경 시 회원 목록 새로고침
  useEffect(() => {
    if (isOpen) {
      loadAvailableMembers();
    }
  }, [isOpen, loadAvailableMembers]);

  // 시간 겹침 체크
  useEffect(() => {
    setTimeConflict(checkTimeConflict);
  }, [checkTimeConflict]);

  // 권한 없음 처리
  if (isOpen && !hasPermission) {
    return (
      <Modal 
        isOpen={isOpen} 
        onClose={handleClose}
        body={
          <div>
            <div style={{ textAlign: 'center', padding: '48px' }}>
              <div style={{ fontSize: AppTextStyles.body1.fontSize, color: AppColors.error, marginBottom: '16px' }}>
                예약 등록 권한이 없습니다.
              </div>
              <div style={{ fontSize: AppTextStyles.body2.fontSize, color: AppColors.onSurface + '80' }}>
                마스터 또는 담당 코치만 예약을 등록할 수 있습니다.
              </div>
            </div>
          </div>
        }
        footer={
          <Button variant="secondary" onClick={handleClose}>
            확인
          </Button>
        }
        width="400px"
      />
    );
  }

  return (
    <Modal 
      isOpen={isOpen} 
      onClose={handleClose}
      body={
        <ModalContent>
          {/* 시간 정보 */}
          <TimeInfo>
            <TimeText>
              {formatDate(startTime)}
              <br />
              {formatTime(startTime)} - {formatTime(endTime)} ({Math.round((endTime.getTime() - startTime.getTime()) / (1000 * 60))}분)
              <br />
              {staffName} 코치 · {programName}
            </TimeText>
          </TimeInfo>

          {/* 시간 겹침 경고 */}
          {timeConflict && (
            <WarningText>
              {timeConflict}
            </WarningText>
          )}

          {/* 회원 검색 */}
          <SearchSection>
            <SectionTitle>수업 참여 회원 선택</SectionTitle>
            <TextField
              label="회원 이름 검색"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="회원 이름을 입력하세요"
            />
          </SearchSection>

          {/* 회원 목록 */}
          <MemberList>
            {loading ? (
              <NoMembers>회원 목록을 불러오는 중...</NoMembers>
            ) : availableMembers.length === 0 ? (
              <NoMembers>
                {searchQuery ? 
                  `"${searchQuery}"와 일치하는 회원이 없습니다.` : 
                  '예약 가능한 회원이 없습니다.\n횟수제 수강권을 가진 활성 회원만 예약 가능합니다.'
                }
              </NoMembers>
            ) : (
              availableMembers.map(member => (
                <MemberItem
                  key={member.id}
                  selected={selectedMember?.id === member.id}
                  onClick={() => {
                    setSelectedMember(member);
                    setMemo(member.remarks || '');
                  }}
                >
                  <MemberInfo>
                    <MemberDetails>
                      <MemberName>{member.name}</MemberName>
                      <MemberPhone>{member.phone}</MemberPhone>
                      <CourseInfo>
                        <span>{member.courseEnrollment?.productName}</span>
                        <span>•</span>
                        <SessionCount low={(member.availableSessions || 0) <= 3}>
                          잔여 {member.availableSessions}회
                        </SessionCount>
                      </CourseInfo>
                      {member.remarks && (
                        <MemberMemo>
                          비고: {member.remarks}
                        </MemberMemo>
                      )}
                    </MemberDetails>
                  </MemberInfo>
                </MemberItem>
              ))
            )}
          </MemberList>

          {/* 메모 섹션 */}
          {selectedMember && (
            <MemoSection>
              <SectionTitle>수업 메모</SectionTitle>
              <MemoTextArea
                value={memo}
                onChange={(e) => setMemo(e.target.value)}
                placeholder="이번 수업에 대한 메모를 입력하세요 (선택사항)"
              />
            </MemoSection>
          )}
        </ModalContent>
      }
      footer={
        <ModalFooter>
          <Button variant="secondary" onClick={handleClose}>
            취소
          </Button>
          <Button 
            variant="primary" 
            onClick={handleCreateReservation}
            disabled={!selectedMember || !!timeConflict || loading}
          >
            {loading ? '등록 중...' : '예약 등록'}
          </Button>
        </ModalFooter>
      }
      width="800px"
    />
  );
};

export default ReservationModal;
