import React from 'react';
import styled from 'styled-components';
import CMSLayout from '../../../components/CMSLayout';
import { AppColors } from '../../../styles/colors';
import { AppTextStyles } from '../../../styles/textStyles';

const PageContainer = styled.div`
  width: 100%;
  max-width: 1000px;
  margin: 0 auto;
`;

const ComingSoonContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  text-align: center;
  background: ${AppColors.surface};
  border: 1px solid ${AppColors.borderLight};
  border-radius: 16px;
  padding: 60px 40px;
`;

const ComingSoonIcon = styled.div`
  font-size: 64px;
  margin-bottom: 20px;
`;

const ComingSoonTitle = styled.h2`
  font-size: ${AppTextStyles.title2.fontSize};
  font-weight: 700;
  color: ${AppColors.onSurface};
  margin: 0 0 12px 0;
`;

const ComingSoonDescription = styled.p`
  font-size: ${AppTextStyles.body1.fontSize};
  color: ${AppColors.onInput1};
  margin: 0 0 24px 0;
  max-width: 400px;
  line-height: 1.5;
`;

const FeatureList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: left;
  color: ${AppColors.onInput1};
  font-size: ${AppTextStyles.body2.fontSize};

  li {
    margin-bottom: 8px;

    &:before {
      content: "✓ ";
      color: ${AppColors.primary};
      font-weight: bold;
      margin-right: 8px;
    }
  }
`;

const ExcelUpload: React.FC = () => {
  return (
    <CMSLayout currentPath="/cms/member/excel-upload">
      <PageContainer>
        <ComingSoonContainer>
          <ComingSoonIcon>📊</ComingSoonIcon>
          <ComingSoonTitle>엑셀 업로드</ComingSoonTitle>
          <ComingSoonDescription>
            엑셀 파일을 이용하여 대량의 회원 데이터를 한 번에 업로드할 수 있는 기능입니다.
          </ComingSoonDescription>
          <FeatureList>
            <li>엑셀 템플릿 다운로드</li>
            <li>회원 정보 일괄 등록</li>
            <li>데이터 검증 및 오류 확인</li>
            <li>업로드 진행률 표시</li>
            <li>업로드 결과 리포트</li>
          </FeatureList>
        </ComingSoonContainer>
      </PageContainer>
    </CMSLayout>
  );
};

export default ExcelUpload;
