import React from 'react';
import { StepContent, StepTitle, FormGrid, FormField, Label, Input, Select, CheckboxLabel, Checkbox } from './StyledComponents';
import { JoinInfo, StepProps } from './types';

const JoinInfoStep: React.FC<StepProps> = ({ formData, onUpdate }) => {
  const coaches = ['한수민', '임정현', '민웅희', '김코치', '이코치'];
  const joinPaths = ['인터넷 검색', '지인 추천', '광고', '직접 방문', '기타'];

  const handleInputChange = (field: keyof JoinInfo, value: string | boolean) => {
    onUpdate({
      joinInfo: { ...formData.joinInfo, [field]: value }
    });
  };

  return (
    <StepContent>
      <StepTitle>가입정보</StepTitle>
      <FormGrid>
        <FormField>
          <Label>담당 코치 *</Label>
          <Select
            value={formData.joinInfo.coach}
            onChange={(e) => handleInputChange('coach', e.target.value)}
          >
            <option value="">선택하세요</option>
            {coaches.map(coach => (
              <option key={coach} value={coach}>{coach}</option>
            ))}
          </Select>
        </FormField>

        <FormField>
          <Label>가입경로 *</Label>
          <Select
            value={formData.joinInfo.joinPath}
            onChange={(e) => handleInputChange('joinPath', e.target.value)}
          >
            <option value="">선택하세요</option>
            {joinPaths.map(path => (
              <option key={path} value={path}>{path}</option>
            ))}
          </Select>
        </FormField>

        <FormField $fullWidth>
          <CheckboxLabel>
            <Checkbox
              checked={formData.joinInfo.enableLogin}
              onChange={(e) => handleInputChange('enableLogin', e.target.checked)}
            />
            로그인 기능 사용 (선택사항)
          </CheckboxLabel>
        </FormField>

        {formData.joinInfo.enableLogin && (
          <>
            <FormField>
              <Label>로그인 아이디</Label>
              <Input
                value={formData.joinInfo.loginId}
                onChange={(e) => handleInputChange('loginId', e.target.value)}
                placeholder="아이디 입력"
              />
            </FormField>

            <FormField>
              <Label>로그인 비밀번호</Label>
              <Input
                type="password"
                value={formData.joinInfo.loginPassword}
                onChange={(e) => handleInputChange('loginPassword', e.target.value)}
                placeholder="비밀번호 입력"
              />
            </FormField>
          </>
        )}
      </FormGrid>
    </StepContent>
  );
};

export default JoinInfoStep;
