import React from 'react';
import styled from 'styled-components';
import CMSLayout from '../../../components/CMSLayout';
import { AppColors } from '../../../styles/colors';
import { AppTextStyles } from '../../../styles/textStyles';

const PageContainer = styled.div`
  width: 100%;
  max-width: 1000px;
  margin: 0 auto;
`;

const ComingSoonContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  text-align: center;
  background: ${AppColors.surface};
  border: 1px solid ${AppColors.borderLight};
  border-radius: 16px;
  padding: 60px 40px;
`;

const ComingSoonIcon = styled.div`
  font-size: 64px;
  margin-bottom: 20px;
`;

const ComingSoonTitle = styled.h2`
  font-size: ${AppTextStyles.title2.fontSize};
  font-weight: 700;
  color: ${AppColors.onSurface};
  margin: 0 0 12px 0;
`;

const ComingSoonDescription = styled.p`
  font-size: ${AppTextStyles.body1.fontSize};
  color: ${AppColors.onInput1};
  margin: 0 0 24px 0;
  max-width: 400px;
  line-height: 1.5;
`;

const FeatureList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: left;
  color: ${AppColors.onInput1};
  font-size: ${AppTextStyles.body2.fontSize};

  li {
    margin-bottom: 8px;

    &:before {
      content: "✓ ";
      color: ${AppColors.primary};
      font-weight: bold;
      margin-right: 8px;
    }
  }
`;

const MemberExport: React.FC = () => {
  return (
    <CMSLayout currentPath="/cms/member/export">
      <PageContainer>
        <ComingSoonContainer>
          <ComingSoonIcon>📤</ComingSoonIcon>
          <ComingSoonTitle>회원 데이터 내보내기</ComingSoonTitle>
          <ComingSoonDescription>
            회원 정보를 다양한 형식으로 내보내어 외부 시스템과 연동하거나 백업할 수 있습니다.
          </ComingSoonDescription>
          <FeatureList>
            <li>엑셀 형식으로 내보내기</li>
            <li>CSV 형식으로 내보내기</li>
            <li>PDF 리포트 생성</li>
            <li>선택적 필드 내보내기</li>
            <li>필터링된 데이터 내보내기</li>
          </FeatureList>
        </ComingSoonContainer>
      </PageContainer>
    </CMSLayout>
  );
};

export default MemberExport;
