`;

const StatusBadge = styled.span<{ $status: string }>`
  padding: 4px 8px;
  border-radius: 12px;
  ${AppTextStyles.label2};
  font-weight: 500;
  background-color: ${props => {
    switch (props.$status) {
      case '완료': return `${AppColors.success}20`;
      case '취소': return `${AppColors.error}20`;
      case '환불': return `${AppColors.warning}20`;
      default: return `${AppColors.onSurface}20`;
    }
  }};
  color: ${props => {
    switch (props.$status) {
      case '완료': return AppColors.success;
      case '취소': return AppColors.error;
      case '환불': return AppColors.warning;
      default: return AppColors.onSurface;
    }
  }};
`;

const EmptyState = styled.div`
  padding: 48px 24px;
  text-align: center;
  color: ${AppColors.onInput2};
`;

// 임시 결제 이력 데이터
const mockPaymentHistory = [
  {
    id: 'P001',
    memberName: '김회원',
    memberId: 'M001',
    amount: 150000,
    paymentMethod: '카드',
    program: 'WBM 프로그램',
    status: '완료',
    date: '2024-01-15',
    description: '3개월 회원권 결제'
  },
  {
    id: 'P002',
    memberName: '이회원',
    memberId: 'M002',
    amount: 80000,
    paymentMethod: '현금',
    program: '릴렉싱 프로그램',
    status: '완료',
    date: '2024-01-14',
    description: '1개월 회원권 결제'
  },
  {
    id: 'P003',
    memberName: '박회원',
    memberId: 'M003',
    amount: 200000,
    paymentMethod: '카드',
    program: 'WBM 프로그램',
    status: '환불',
    date: '2024-01-13',
    description: '6개월 회원권 환불'
  },
];

const MemberPaymentHistory: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('전체');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [paymentHistory] = useState(mockPaymentHistory);
  const [filteredHistory, setFilteredHistory] = useState(mockPaymentHistory);

  const handleSearch = () => {
    let filtered = paymentHistory;

    // 검색어 필터링
    if (searchTerm) {
      filtered = filtered.filter(payment => 
        payment.memberName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        payment.memberId.toLowerCase().includes(searchTerm.toLowerCase()) ||
        payment.program.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // 상태 필터링
    if (selectedStatus !== '전체') {
      filtered = filtered.filter(payment => payment.status === selectedStatus);
    }

    // 날짜 필터링
    if (startDate && endDate) {
      filtered = filtered.filter(payment => {
        const paymentDate = new Date(payment.date);
        const start = new Date(startDate);
        const end = new Date(endDate);
        return paymentDate >= start && paymentDate <= end;
      });
    }

    setFilteredHistory(filtered);
  };

  const formatAmount = (amount: number) => {
    return new Intl.NumberFormat('ko-KR').format(amount) + '원';
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  };

  useEffect(() => {
    const initialSearch = () => {
      setFilteredHistory(paymentHistory);
    };
    initialSearch();
  }, [paymentHistory]);

  return (
    <Container>
      <Header>
        <Title>결제 이력</Title>
        <Description>회원들의 결제 이력을 조회하고 관리할 수 있습니다.</Description>
      </Header>

      <SearchSection>
        <SearchForm>
          <FormField>
            <Label>검색어</Label>
            <Input
              type="text"
              placeholder="회원명, 회원ID, 프로그램명"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </FormField>

          <FormField>
            <Label>결제 상태</Label>
            <Select
              value={selectedStatus}
              onChange={(e) => setSelectedStatus(e.target.value)}
            >
              <option value="전체">전체</option>
              <option value="완료">완료</option>
              <option value="취소">취소</option>
              <option value="환불">환불</option>
            </Select>
          </FormField>

          <FormField>
            <Label>기간 조회</Label>
            <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
              <Input
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
              />
              <span>~</span>
              <Input
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
              />
            </div>
          </FormField>

          <SearchButton onClick={handleSearch}>
            검색
          </SearchButton>
        </SearchForm>
      </SearchSection>

      <ResultsSection>
        <ResultsHeader>
          <ResultsTitle>결제 이력 목록</ResultsTitle>
          <ResultsCount>총 {filteredHistory.length}건</ResultsCount>
        </ResultsHeader>

        {filteredHistory.length > 0 ? (
          <Table>
            <TableHeader>
              <tr>
                <TableHeaderCell>결제일</TableHeaderCell>
                <TableHeaderCell>회원명</TableHeaderCell>
                <TableHeaderCell>회원ID</TableHeaderCell>
                <TableHeaderCell>프로그램</TableHeaderCell>
                <TableHeaderCell>결제금액</TableHeaderCell>
                <TableHeaderCell>결제방법</TableHeaderCell>
                <TableHeaderCell>상태</TableHeaderCell>
                <TableHeaderCell>내용</TableHeaderCell>
              </tr>
            </TableHeader>
            <TableBody>
              {filteredHistory.map((payment) => (
                <TableRow key={payment.id}>
                  <TableCell>{formatDate(payment.date)}</TableCell>
                  <TableCell>{payment.memberName}</TableCell>
                  <TableCell>{payment.memberId}</TableCell>
                  <TableCell>{payment.program}</TableCell>
                  <TableCell>{formatAmount(payment.amount)}</TableCell>
                  <TableCell>{payment.paymentMethod}</TableCell>
                  <TableCell>
                    <StatusBadge $status={payment.status}>
                      {payment.status}
                    </StatusBadge>
                  </TableCell>
                  <TableCell>{payment.description}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        ) : (
          <EmptyState>
            검색 조건에 맞는 결제 이력이 없습니다.
          </EmptyState>
        )}
      </ResultsSection>
    </Container>
  );
};

export default MemberPaymentHistory;
