+++ 
@@ -0,0 +1,285 @@
+import React, { useState } from 'react';
+import { 
+  PageContainer, 
+  PageHeader, 
+  PageTitle, 
+  PageDescription,
+  StepContainer,
+  StepWrapper,
+  StepItem,
+  StepCircle,
+  StepLabel,
+  StepLine,
+  Card,
+  ButtonGroup,
+  Button
+} from './StyledComponents';
+import { MemberFormData } from './types';
+import BasicInfoStep from './BasicInfoStep';
+import JoinInfoStep from './JoinInfoStep';
+import PaymentInfoStep from './PaymentInfoStep';
+import AgreementStep from './AgreementStep';
+
+const MemberRegister: React.FC = () => {
+  const [currentStep, setCurrentStep] = useState(1);
+  const [formData, setFormData] = useState<MemberFormData>({
+    basicInfo: {
+      name: '',
+      phone: '',
+      email: '',
+      birth: '',
+      gender: '',
+      address: '',
+    },
+    joinInfo: {
+      coach: '',
+      joinPath: '',
+      loginId: '',
+      loginPassword: '',
+      enableLogin: false,
+    },
+    paymentInfo: {
+      selectedProducts: [],
+    },
+    agreementInfo: {
+      agreements: [
+        {
+          id: 'terms',
+          title: '회원이용약관',
+          content: '회원이용약관 내용입니다...',
+          required: true,
+          agreed: false,
+        },
+        {
+          id: 'privacy',
+          title: '개인정보처리방침',
+          content: '개인정보처리방침 내용입니다...',
+          required: true,
+          agreed: false,
+        },
+        {
+          id: 'service',
+          title: '서비스이용약관',
+          content: '서비스이용약관 내용입니다...',
+          required: true,
+          agreed: false,
+        },
+        {
+          id: 'marketing',
+          title: '마케팅활용 동의',
+          content: '마케팅활용 동의 내용입니다...',
+          required: false,
+          agreed: false,
+        },
+      ],
+      customerSignature: '',
+      staffSignature: '',
+    },
+  });
+
+  const steps = [
+    { number: 1, label: '기본정보' },
+    { number: 2, label: '가입정보' },
+    { number: 3, label: '결제정보' },
+    { number: 4, label: '동의서명' },
+  ];
+
+  const handleFormUpdate = (updatedData: Partial<MemberFormData>) => {
+    setFormData(prev => ({ ...prev, ...updatedData }));
+  };
+
+  const validateCurrentStep = (): boolean => {
+    switch (currentStep) {
+      case 1:
+        return !!(formData.basicInfo.name && formData.basicInfo.phone);
+      case 2:
+        return !!(formData.joinInfo.coach && formData.joinInfo.joinPath);
+      case 3:
+        return true; // 결제정보는 선택사항
+      case 4:
+        const requiredAgreements = formData.agreementInfo.agreements.filter(a => a.required);
+        return requiredAgreements.every(a => a.agreed) && 
+               !!(formData.agreementInfo.customerSignature && formData.agreementInfo.staffSignature);
+      default:
+        return true;
+    }
+  };
+
+  const handleNext = () => {
+    if (validateCurrentStep() && currentStep < 4) {
+      setCurrentStep(currentStep + 1);
+    }
+  };
+
+  const handlePrev = () => {
+    if (currentStep > 1) {
+      setCurrentStep(currentStep - 1);
+    }
+  };
+
+  const handleSkipPayment = () => {
+    setCurrentStep(4);
+  };
+
+  const handleSubmit = () => {
+    console.log('회원 등록 데이터:', formData);
+    alert('회원이 성공적으로 등록되었습니다!');
+    
+    // 등록 완료 후 초기화
+    setCurrentStep(1);
+    setFormData({
+      basicInfo: {
+        name: '',
+        phone: '',
+        email: '',
+        birth: '',
+        gender: '',
+        address: '',
+      },
+      joinInfo: {
+        coach: '',
+        joinPath: '',
+        loginId: '',
+        loginPassword: '',
+        enableLogin: false,
+      },
+      paymentInfo: {
+        selectedProducts: [],
+      },
+      agreementInfo: {
+        agreements: [
+          {
+            id: 'terms',
+            title: '회원이용약관',
+            content: '회원이용약관 내용입니다...',
+            required: true,
+            agreed: false,
+          },
+          {
+            id: 'privacy',
+            title: '개인정보처리방침',
+            content: '개인정보처리방침 내용입니다...',
+            required: true,
+            agreed: false,
+          },
+          {
+            id: 'service',
+            title: '서비스이용약관',
+            content: '서비스이용약관 내용입니다...',
+            required: true,
+            agreed: false,
+          },
+          {
+            id: 'marketing',
+            title: '마케팅활용 동의',
+            content: '마케팅활용 동의 내용입니다...',
+            required: false,
+            agreed: false,
+          },
+        ],
+        customerSignature: '',
+        staffSignature: '',
+      },
+    });
+  };
+
+  const renderStepContent = () => {
+    const stepProps = {
+      formData,
+      onUpdate: handleFormUpdate,
+      onNext: handleNext,
+      onPrev: handlePrev,
+      isValid: validateCurrentStep(),
+    };
+
+    switch (currentStep) {
+      case 1:
+        return <BasicInfoStep {...stepProps} />;
+      case 2:
+        return <JoinInfoStep {...stepProps} />;
+      case 3:
+        return <PaymentInfoStep {...stepProps} />;
+      case 4:
+        return <AgreementStep {...stepProps} />;
+      default:
+        return null;
+    }
+  };
+
+  return (
+    <PageContainer>
+      <PageHeader>
+        <PageTitle>신규 회원 등록</PageTitle>
+        <PageDescription>새로운 회원의 정보를 단계별로 입력하여 등록합니다.</PageDescription>
+      </PageHeader>
+
+      <StepContainer>
+        <StepWrapper>
+          {steps.map((step, index) => (
+            <StepItem
+              key={step.number}
+              $active={currentStep === step.number}
+              $completed={currentStep > step.number}
+            >
+              <StepCircle
+                $active={currentStep === step.number}
+                $completed={currentStep > step.number}
+              >
+                {currentStep > step.number ? '✓' : step.number}
+              </StepCircle>
+              <StepLabel
+                $active={currentStep === step.number}
+                $completed={currentStep > step.number}
+              >
+                {step.label}
+              </StepLabel>
+              {index < steps.length - 1 && (
+                <StepLine $completed={currentStep > step.number} />
+              )}
+            </StepItem>
+          ))}
+        </StepWrapper>
+      </StepContainer>
+
+      <Card>
+        {renderStepContent()}
+
+        <ButtonGroup>
+          <div>
+            {currentStep > 1 && (
+              <Button variant="secondary" onClick={handlePrev}>
+                이전
+              </Button>
+            )}
+          </div>
+
+          <div style={{ display: 'flex', gap: '12px' }}>
+            {currentStep === 3 && (
+              <Button variant="outline" onClick={handleSkipPayment}>
+                건너뛰기
+              </Button>
+            )}
+            
+            {currentStep < 4 ? (
+              <Button 
+                onClick={handleNext} 
+                disabled={!validateCurrentStep()}
+              >
+                다음
+              </Button>
+            ) : (
+              <Button 
+                onClick={handleSubmit}
+                disabled={!validateCurrentStep()}
+              >
+                회원등록 완료
+              </Button>
+            )}
+          </div>
+        </ButtonGroup>
+      </Card>
+    </PageContainer>
+  );
+};
+
+export default MemberRegister;

