import React, { useState } from 'react';
import styled from 'styled-components';
import CMSLayout from '../../../components/CMSLayout';
import { AppColors } from '../../../styles/colors';
import { AppTextStyles } from '../../../styles/textStyles';

const PageContainer = styled.div`
  width: 100%;
  max-width: 800px;
  margin: 0 auto;
`;

const PageHeader = styled.div`
  margin-bottom: 24px;
`;

const PageTitle = styled.h1`
  font-size: ${AppTextStyles.title1.fontSize};
  font-weight: 700;
  color: ${AppColors.onBackground};
  margin: 0 0 8px 0;
`;

const PageDescription = styled.p`
  font-size: ${AppTextStyles.body1.fontSize};
  color: ${AppColors.onInput1};
  margin: 0;
`;

const Card = styled.div`
  background: ${AppColors.surface};
  border: 1px solid ${AppColors.borderLight};
  border-radius: 16px;
  padding: 24px;
  box-shadow: 0 1px 2px rgba(16, 24, 40, 0.04);
`;

const FormGrid = styled.div`
  display: grid;
  gap: 16px;
  grid-template-columns: 1fr;

  @media (min-width: 768px) {
    grid-template-columns: 1fr 1fr;
  }
`;

const FormField = styled.div<{ $fullWidth?: boolean }>`
  display: flex;
  flex-direction: column;
  gap: 8px;
  grid-column: ${props => props.$fullWidth ? '1 / -1' : 'auto'};
`;

const Label = styled.label`
  font-size: ${AppTextStyles.label1.fontSize};
  font-weight: 600;
  color: ${AppColors.onSurface};
`;

const Input = styled.input`
  padding: 12px;
  border: 1px solid ${AppColors.borderLight};
  border-radius: 8px;
  font-size: ${AppTextStyles.body1.fontSize};
  outline: none;

  &:focus {
    border-color: ${AppColors.primary};
  }

  &::placeholder {
    color: ${AppColors.onInput1};
  }
`;

const Select = styled.select`
  padding: 12px;
  border: 1px solid ${AppColors.borderLight};
  border-radius: 8px;
  font-size: ${AppTextStyles.body1.fontSize};
  outline: none;
  background-color: ${AppColors.surface};

  &:focus {
    border-color: ${AppColors.primary};
  }
`;

const TextArea = styled.textarea`
  padding: 12px;
  border: 1px solid ${AppColors.borderLight};
  border-radius: 8px;
  font-size: ${AppTextStyles.body1.fontSize};
  outline: none;
  min-height: 100px;
  resize: vertical;

  &:focus {
    border-color: ${AppColors.primary};
  }

  &::placeholder {
    color: ${AppColors.onInput1};
  }
`;

const CheckboxGroup = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 12px;
`;

const CheckboxLabel = styled.label`
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: ${AppTextStyles.body2.fontSize};
  cursor: pointer;
`;

const Checkbox = styled.input.attrs({ type: 'checkbox' })`
  width: 18px;
  height: 18px;
  accent-color: ${AppColors.primary};
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 12px;
  justify-content: flex-end;
  margin-top: 24px;
`;

const Button = styled.button<{ variant?: 'primary' | 'secondary' }>`
  padding: 12px 24px;
  border: ${props => props.variant === 'secondary' ? `1px solid ${AppColors.borderLight}` : 'none'};
  border-radius: 8px;
  background: ${props => props.variant === 'secondary' ? AppColors.surface : AppColors.primary};
  color: ${props => props.variant === 'secondary' ? AppColors.onSurface : AppColors.onPrimary};
  font-size: ${AppTextStyles.body1.fontSize};
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    opacity: 0.9;
  }
`;

interface MemberFormData {
  name: string;
  phone: string;
  email: string;
  birth: string;
  gender: string;
  address: string;
  emergencyContact: string;
  emergencyPhone: string;
  tags: string[];
  coach: string;
  notes: string;
}

const MemberRegister: React.FC = () => {
  const [formData, setFormData] = useState<MemberFormData>({
    name: '',
    phone: '',
    email: '',
    birth: '',
    gender: '',
    address: '',
    emergencyContact: '',
    emergencyPhone: '',
    tags: [],
    coach: '',
    notes: '',
  });

  const availableTags = ['WBM', '릴렉싱', 'VIP', '신규', '재등록'];
  const coaches = ['한수민', '임정현', '민웅희', '김코치', '이코치'];

  const handleInputChange = (field: keyof MemberFormData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleTagChange = (tag: string, checked: boolean) => {
    setFormData(prev => ({
      ...prev,
      tags: checked 
        ? [...prev.tags, tag]
        : prev.tags.filter(t => t !== tag)
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('회원 등록 데이터:', formData);
    alert('회원이 성공적으로 등록되었습니다!');
  };

  const handleCancel = () => {
    if (window.confirm('작성 중인 내용이 사라집니다. 정말 취소하시겠습니까?')) {
      setFormData({
        name: '',
        phone: '',
        email: '',
        birth: '',
        gender: '',
        address: '',
        emergencyContact: '',
        emergencyPhone: '',
        tags: [],
        coach: '',
        notes: '',
      });
    }
  };

  return (
    <CMSLayout currentPath="/cms/member/register">
      <PageContainer>
        <PageHeader>
          <PageTitle>신규 회원 등록</PageTitle>
          <PageDescription>새로운 회원의 정보를 입력하여 등록합니다.</PageDescription>
        </PageHeader>

        <Card>
          <form onSubmit={handleSubmit}>
            <FormGrid>
              <FormField>
                <Label>이름 *</Label>
                <Input
                  value={formData.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  placeholder="홍길동"
                  required
                />
              </FormField>

              <FormField>
                <Label>연락처 *</Label>
                <Input
                  type="tel"
                  value={formData.phone}
                  onChange={(e) => handleInputChange('phone', e.target.value)}
                  placeholder="010-0000-0000"
                  required
                />
              </FormField>

              <FormField>
                <Label>이메일</Label>
                <Input
                  type="email"
                  value={formData.email}
                  onChange={(e) => handleInputChange('email', e.target.value)}
                  placeholder="example@email.com"
                />
              </FormField>

              <FormField>
                <Label>생년월일</Label>
                <Input
                  type="date"
                  value={formData.birth}
                  onChange={(e) => handleInputChange('birth', e.target.value)}
                />
              </FormField>

              <FormField>
                <Label>성별</Label>
                <Select
                  value={formData.gender}
                  onChange={(e) => handleInputChange('gender', e.target.value)}
                >
                  <option value="">선택하세요</option>
                  <option value="male">남성</option>
                  <option value="female">여성</option>
                </Select>
              </FormField>

              <FormField>
                <Label>담당 코치</Label>
                <Select
                  value={formData.coach}
                  onChange={(e) => handleInputChange('coach', e.target.value)}
                >
                  <option value="">선택하세요</option>
                  {coaches.map(coach => (
                    <option key={coach} value={coach}>{coach}</option>
                  ))}
                </Select>
              </FormField>

              <FormField $fullWidth>
                <Label>주소</Label>
                <Input
                  value={formData.address}
                  onChange={(e) => handleInputChange('address', e.target.value)}
                  placeholder="서울시 강남구..."
                />
              </FormField>

              <FormField>
                <Label>비상연락처 (이름)</Label>
                <Input
                  value={formData.emergencyContact}
                  onChange={(e) => handleInputChange('emergencyContact', e.target.value)}
                  placeholder="김가족"
                />
              </FormField>

              <FormField>
                <Label>비상연락처 (전화번호)</Label>
                <Input
                  type="tel"
                  value={formData.emergencyPhone}
                  onChange={(e) => handleInputChange('emergencyPhone', e.target.value)}
                  placeholder="010-0000-0000"
                />
              </FormField>

              <FormField $fullWidth>
                <Label>태그</Label>
                <CheckboxGroup>
                  {availableTags.map(tag => (
                    <CheckboxLabel key={tag}>
                      <Checkbox
                        checked={formData.tags.includes(tag)}
                        onChange={(e) => handleTagChange(tag, e.target.checked)}
                      />
                      {tag}
                    </CheckboxLabel>
                  ))}
                </CheckboxGroup>
              </FormField>

              <FormField $fullWidth>
                <Label>특이사항</Label>
                <TextArea
                  value={formData.notes}
                  onChange={(e) => handleInputChange('notes', e.target.value)}
                  placeholder="회원에 대한 특이사항이나 주의사항을 입력하세요..."
                />
              </FormField>
            </FormGrid>

            <ButtonGroup>
              <Button type="button" variant="secondary" onClick={handleCancel}>
                취소
              </Button>
              <Button type="submit">
                등록하기
              </Button>
            </ButtonGroup>
          </form>
        </Card>
      </PageContainer>
    </CMSLayout>
  );
};

export default MemberRegister;
