import React from 'react';
import styled from 'styled-components';
import CMSLayout from '../../../components/CMSLayout';
import { AppColors } from '../../../styles/colors';
import { AppTextStyles } from '../../../styles/textStyles';

const PageContainer = styled.div`
  width: 100%;
  max-width: 1000px;
  margin: 0 auto;
`;

const ComingSoonContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  text-align: center;
  background: ${AppColors.surface};
  border: 1px solid ${AppColors.borderLight};
  border-radius: 16px;
  padding: 60px 40px;
`;

const ComingSoonIcon = styled.div`
  font-size: 64px;
  margin-bottom: 20px;
`;

const ComingSoonTitle = styled.h2`
  font-size: ${AppTextStyles.title2.fontSize};
  font-weight: 700;
  color: ${AppColors.onSurface};
  margin: 0 0 12px 0;
`;

const ComingSoonDescription = styled.p`
  font-size: ${AppTextStyles.body1.fontSize};
  color: ${AppColors.onInput1};
  margin: 0 0 24px 0;
  max-width: 400px;
  line-height: 1.5;
`;

const FeatureList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: left;
  color: ${AppColors.onInput1};
  font-size: ${AppTextStyles.body2.fontSize};

  li {
    margin-bottom: 8px;

    &:before {
      content: "✓ ";
      color: ${AppColors.primary};
      font-weight: bold;
      margin-right: 8px;
    }
  }
`;

const VisitRecord: React.FC = () => {
  return (
    <CMSLayout currentPath="/cms/member/visit-record">
      <PageContainer>
        <ComingSoonContainer>
          <ComingSoonIcon>📋</ComingSoonIcon>
          <ComingSoonTitle>내원 기록 관리</ComingSoonTitle>
          <ComingSoonDescription>
            회원들의 내원 기록을 상세하게 관리하고 추적할 수 있는 기능입니다.
          </ComingSoonDescription>
          <FeatureList>
            <li>회원별 내원 이력 조회</li>
            <li>프로그램별 이용 현황</li>
            <li>내원 패턴 분석</li>
            <li>출석률 통계</li>
            <li>내원 기록 수정/삭제</li>
          </FeatureList>
        </ComingSoonContainer>
      </PageContainer>
    </CMSLayout>
  );
};

export default VisitRecord;
