import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { AppColors } from '../../../styles/colors';
import { AppTextStyles } from '../../../styles/textStyles';
import CMSLayout from '../../../components/CMSLayout';
import { dbManager, Program } from '../../../utils/indexedDB';

const Container = styled.div`
  width: 100%;
`;

const ContentContainer = styled.div`
  padding: 24px;
  background-color: ${AppColors.surface};
  border-radius: 8px;
  border: 1px solid ${AppColors.borderLight};
`;

const Title = styled.h2`
  font-size: ${AppTextStyles.title1.fontSize};
  font-weight: 600;
  color: ${AppColors.onSurface};
  margin-bottom: 24px;
`;

const ProgramInfo = styled.div`
  padding: 16px;
  background-color: ${AppColors.background};
  border-radius: 8px;
  border: 1px solid ${AppColors.borderLight};
  margin-bottom: 24px;
`;

const ProgramName = styled.h3`
  font-size: ${AppTextStyles.title2.fontSize};
  font-weight: 500;
  color: ${AppColors.primary};
  margin-bottom: 8px;
`;

const ProgramDetails = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
`;

const DetailItem = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: ${AppTextStyles.body2.fontSize};
  color: ${AppColors.onSurface};
`;

const DetailLabel = styled.span`
  font-weight: 500;
  min-width: 80px;
  color: ${AppColors.onSurface}80;
`;

const DetailValue = styled.span`
  color: ${AppColors.onSurface};
`;

const PlaceholderContent = styled.div`
  padding: 48px;
  text-align: center;
  color: ${AppColors.onSurface}60;
  font-size: ${AppTextStyles.body1.fontSize};
  border: 2px dashed ${AppColors.borderLight};
  border-radius: 8px;
`;

const ReservationPage: React.FC = () => {
  const { programId } = useParams<{ programId: string }>();
  const location = useLocation();
  const [program, setProgram] = useState<Program | null>(null);
  const [loading, setLoading] = useState(false);

  // 현재 선택된 메뉴 확인
  const getMenuType = () => {
    const path = location.pathname;
    if (path.includes('/program/')) return 'program';
    if (path.includes('/new')) return 'new';
    if (path.includes('/timetable')) return 'timetable';
    if (path.includes('/batch')) return 'batch';
    if (path.includes('/waiting')) return 'waiting';
    if (path.includes('/auto-remind')) return 'auto-remind';
    return 'unknown';
  };

  const getPageTitle = () => {
    const menuType = getMenuType();
    switch (menuType) {
      case 'program':
        return program ? `${program.name} 예약` : '프로그램 예약';
      case 'new':
        return '신규 예약';
      case 'timetable':
        return '시간표 보기';
      case 'batch':
        return '일괄 예약';
      case 'waiting':
        return '대기 관리';
      case 'auto-remind':
        return '자동 리마인드';
      default:
        return '예약/스케줄';
    }
  };

  // 프로그램 정보 로드
  useEffect(() => {
    const loadProgram = async () => {
      if (programId) {
        try {
          setLoading(true);
          const programs = await dbManager.getAllPrograms();
          const foundProgram = programs.find(p => p.id === programId);
          setProgram(foundProgram || null);
        } catch (error) {
          console.error('프로그램 정보 로드 실패:', error);
          setProgram(null);
        } finally {
          setLoading(false);
        }
      }
    };

    loadProgram();
  }, [programId]);

  return (
    <CMSLayout>
      <Container>
        <ContentContainer>
          <Title>{getPageTitle()}</Title>

          {/* 프로그램별 예약인 경우 프로그램 정보 표시 */}
          {program && (
            <ProgramInfo>
              <ProgramName>{program.name}</ProgramName>
              <ProgramDetails>
                <DetailItem>
                  <DetailLabel>유형:</DetailLabel>
                  <DetailValue>{program.type}</DetailValue>
                </DetailItem>
                <DetailItem>
                  <DetailLabel>활성 상태:</DetailLabel>
                  <DetailValue>{program.isActive ? '활성' : '비활성'}</DetailValue>
                </DetailItem>
              </ProgramDetails>
            </ProgramInfo>
          )}

          {loading ? (
            <PlaceholderContent>
              프로그램 정보를 불러오는 중...
            </PlaceholderContent>
          ) : (
            <PlaceholderContent>
              {getPageTitle()} 기능이 준비 중입니다.
              <br />
              <br />
              이곳에 실제 예약 관리 기능이 구현될 예정입니다.
            </PlaceholderContent>
          )}
        </ContentContainer>
      </Container>
    </CMSLayout>
  );
};

export default ReservationPage;
