import React from 'react';
import styled from 'styled-components';
import { AppColors } from '../../../styles/colors';
import { AppTextStyles } from '../../../styles/textStyles';
import BranchManagement from './BranchManagement';
import PositionTable from './PositionTable';
import RoleTable from './RoleTable';
import EmploymentTypeTable from './EmploymentTypeTable';
import PermissionTable from './PermissionTable';

const Container = styled.div`
  padding: 24px;
  min-height: calc(100vh - 140px);
  background-color: ${AppColors.background};
`;

const Header = styled.div`
  margin-bottom: 32px;
`;

const Title = styled.h1`
  font-size: ${AppTextStyles.title1.fontSize};
  font-weight: 700;
  color: ${AppColors.onBackground};
  margin: 0 0 8px 0;
`;

const Subtitle = styled.p`
  font-size: ${AppTextStyles.body1.fontSize};
  color: ${AppColors.onSurface}80;
  margin: 0;
`;

const TablesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
  gap: 24px;
  max-width: 1400px;
`;

const TableSection = styled.div`
  background-color: ${AppColors.surface};
  border-radius: 12px;
  padding: 24px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  border: 1px solid ${AppColors.borderLight};
`;

const SectionTitle = styled.h2`
  font-size: ${AppTextStyles.title3.fontSize};
  font-weight: 600;
  color: ${AppColors.onSurface};
  margin: 0 0 16px 0;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const StatusBadge = styled.span<{ $editable?: boolean }>`
  padding: 4px 8px;
  border-radius: 4px;
  font-size: ${AppTextStyles.label3.fontSize};
  font-weight: 500;
  background-color: ${props => props.$editable ? `${AppColors.success}15` : `${AppColors.warning}15`};
  color: ${props => props.$editable ? AppColors.success : AppColors.warning};
`;

const SettingsPage: React.FC = () => {
  return (
    <Container>
      <Header>
        <Title>시스템 설정</Title>
        <Subtitle>기본 데이터 및 조직 구조를 관리합니다</Subtitle>
      </Header>

      <TablesGrid>
        {/* 지점 등록 - 수정 가능 */}
        <TableSection>
          <SectionTitle>
            지점 등록
            <StatusBadge $editable>편집 가능</StatusBadge>
          </SectionTitle>
          <BranchManagement />
        </TableSection>

        {/* 직급 - 고정 */}
        <TableSection>
          <SectionTitle>
            직급
            <StatusBadge>고정 데이터</StatusBadge>
          </SectionTitle>
          <PositionTable />
        </TableSection>

        {/* 직책 - 고정 */}
        <TableSection>
          <SectionTitle>
            직책
            <StatusBadge>고정 데이터</StatusBadge>
          </SectionTitle>
          <RoleTable />
        </TableSection>

        {/* 고용형태 - 고정 */}
        <TableSection>
          <SectionTitle>
            고용형태
            <StatusBadge>고정 데이터</StatusBadge>
          </SectionTitle>
          <EmploymentTypeTable />
        </TableSection>

        {/* 권한 - 고정 */}
        <TableSection>
          <SectionTitle>
            권한
            <StatusBadge>고정 데이터</StatusBadge>
          </SectionTitle>
          <PermissionTable />
        </TableSection>
      </TablesGrid>
    </Container>
  );
};

export default SettingsPage;
