+        staff.name.toLowerCase().includes(query) ||
+        staff.loginId.toLowerCase().includes(query) ||
+        staff.phone.includes(query) ||
+        staff.email.toLowerCase().includes(query) ||
+        staff.position.toLowerCase().includes(query) ||
