import React, { useState } from 'react';
import styled from 'styled-components';
import CMSLayout from '../../../components/CMSLayout';
import { AppColors } from '../../../styles/colors';
import { AppTextStyles } from '../../../styles/textStyles';

const PageContainer = styled.div`
  width: 100%;
`;

const SearchSection = styled.div`
  background: ${AppColors.surface};
  border: 1px solid ${AppColors.borderLight};
  border-radius: 16px;
  padding: 24px;
  margin-bottom: 24px;
  box-shadow: 0 1px 2px rgba(16, 24, 40, 0.04);
`;

const SearchRow = styled.div`
  display: flex;
  gap: 12px;
  align-items: center;
  margin-bottom: 16px;
`;

const SearchInput = styled.input`
  flex: 1;
  padding: 12px;
  border: 1px solid ${AppColors.borderLight};
  border-radius: 8px;
  font-size: ${AppTextStyles.body1.fontSize};
  outline: none;

  &:focus {
    border-color: ${AppColors.primary};
  }

  &::placeholder {
    color: ${AppColors.onInput1};
  }
`;

const Button = styled.button<{ variant?: 'primary' | 'secondary' }>`
  padding: 12px 18px;
  border: ${props => props.variant === 'secondary' ? `1px solid ${AppColors.borderLight}` : 'none'};
  border-radius: 8px;
  background: ${props => props.variant === 'secondary' ? AppColors.surface : AppColors.primary};
  color: ${props => props.variant === 'secondary' ? AppColors.onSurface : AppColors.onPrimary};
  font-size: ${AppTextStyles.body1.fontSize};
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    opacity: 0.9;
  }
`;

const FilterRow = styled.div<{ $visible: boolean }>`
  display: ${props => props.$visible ? 'flex' : 'none'};
  gap: 8px;
  flex-wrap: wrap;
  align-items: center;
`;

const FilterLabel = styled.span`
  font-size: ${AppTextStyles.label1.fontSize};
  color: ${AppColors.onSurface};
  margin-right: 8px;
`;

const FilterChip = styled.button<{ $active?: boolean }>`
  padding: 6px 12px;
  border-radius: 16px;
  border: 1px solid ${props => props.$active ? AppColors.primary : AppColors.borderLight};
  background: ${props => props.$active ? AppColors.primary : AppColors.surface};
  color: ${props => props.$active ? AppColors.onPrimary : AppColors.onSurface};
  font-size: ${AppTextStyles.label2.fontSize};
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    background: ${props => props.$active ? AppColors.primary : AppColors.primary}10;
  }
`;

const ResultsSection = styled.div`
  background: ${AppColors.surface};
  border: 1px solid ${AppColors.borderLight};
  border-radius: 16px;
  box-shadow: 0 1px 2px rgba(16, 24, 40, 0.04);
`;

const ResultsHeader = styled.div`
  padding: 16px 20px;
  border-bottom: 1px solid ${AppColors.borderLight};
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ResultsTitle = styled.h3`
  font-size: ${AppTextStyles.title3.fontSize};
  font-weight: 600;
  margin: 0;
`;

const ResultsCount = styled.span`
  font-size: ${AppTextStyles.body2.fontSize};
  color: ${AppColors.onInput1};
`;

const TableContainer = styled.div`
  overflow-x: auto;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const TableHeader = styled.thead`
  background: ${AppColors.background};
`;

const TableRow = styled.tr`
  &:hover td {
    background: ${AppColors.primary}05;
  }

  &:not(:last-child) td {
    border-bottom: 1px solid ${AppColors.borderLight};
  }
`;

const TableHeaderCell = styled.th`
  padding: 12px 16px;
  text-align: left;
  font-weight: 600;
  font-size: ${AppTextStyles.label1.fontSize};
  color: ${AppColors.onSurface};
`;

const TableCell = styled.td`
  padding: 12px 16px;
  font-size: ${AppTextStyles.body2.fontSize};
`;

const Tag = styled.span`
  display: inline-block;
  padding: 4px 8px;
  background: ${AppColors.primary}10;
  color: ${AppColors.primary};
  border-radius: 12px;
  font-size: 12px;
  margin-right: 4px;
`;

const ActionButton = styled.button`
  padding: 6px 12px;
  border: 1px solid ${AppColors.borderLight};
  border-radius: 6px;
  background: ${AppColors.surface};
  color: ${AppColors.primary};
  font-size: ${AppTextStyles.label2.fontSize};
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    background: ${AppColors.primary}10;
  }
`;

const EmptyState = styled.div`
  padding: 60px 20px;
  text-align: center;
  color: ${AppColors.onInput1};
`;

interface Staff {
  id: string;
  name: string;
  phone: string;
  email: string;
  position: string;
  department: string;
  permissions: string[];
  startDate: string;
  status: 'active' | 'inactive' | 'leave';
}

const DUMMY_STAFF: Staff[] = [
  {
    id: "EMP001",
    name: "한수민",
    phone: "010-1111-2222",
    email: "han@lavida.com",
    position: "코치",
    department: "코칭팀",
    permissions: ["회원관리", "운동처방"],
    startDate: "2024-01-15",
    status: "active"
  },
  {
    id: "EMP002",
    name: "임정현",
    phone: "010-3333-4444",
    email: "lim@lavida.com",
    position: "매니저",
    department: "운영팀",
    permissions: ["회원관리", "예약관리", "결제관리"],
    startDate: "2023-06-01",
    status: "active"
  },
  {
    id: "EMP003",
    name: "민웅희",
    phone: "010-5555-6666",
    email: "min@lavida.com",
    position: "코치",
    department: "코칭팀",
    permissions: ["회원관리", "운동처방"],
    startDate: "2024-03-10",
    status: "active"
  },
  {
    id: "EMP004",
    name: "김대표",
    phone: "010-7777-8888",
    email: "ceo@lavida.com",
    position: "대표",
    department: "운영팀",
    permissions: ["회원관리", "예약관리", "운동처방", "결제관리", "설정관리"],
    startDate: "2023-01-01",
    status: "active"
  },
];

const StaffSearch: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [filteredStaff, setFilteredStaff] = useState<Staff[]>(DUMMY_STAFF);
  const [showFilters, setShowFilters] = useState<boolean>(false);
  const [activeFilters, setActiveFilters] = useState<string[]>([]);

  const handleSearch = () => {
    let filtered = DUMMY_STAFF;

    // 텍스트 검색
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase().trim();
      filtered = filtered.filter(staff =>
        staff.id.toLowerCase().includes(query) ||
        staff.name.toLowerCase().includes(query) ||
        staff.phone.includes(query) ||
        staff.email.toLowerCase().includes(query) ||
        staff.position.toLowerCase().includes(query) ||
        staff.department.toLowerCase().includes(query)
      );
    }

    // 필터 적용
    if (activeFilters.length > 0) {
      filtered = filtered.filter(staff => {
        return activeFilters.some(filter => {
          switch (filter) {
            case '코치':
              return staff.position === '코치';
            case '매니저':
              return staff.position === '매니저';
            case '대표':
              return staff.position === '대표';
            case '운영팀':
              return staff.department === '운영팀';
            case '코칭팀':
              return staff.department === '코칭팀';
            case '활성':
              return staff.status === 'active';
            case '비활성':
              return staff.status === 'inactive';
            default:
              return true;
          }
        });
      });
    }

    setFilteredStaff(filtered);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const toggleFilter = (filter: string) => {
    setActiveFilters(prev => 
      prev.includes(filter) 
        ? prev.filter(f => f !== filter)
        : [...prev, filter]
    );
  };

  const handleStaffAction = (staffId: string, action: string) => {
    alert(`직원 ${staffId}에 대한 ${action} 작업을 수행합니다.`);
  };

  const clearFilters = () => {
    setActiveFilters([]);
    setSearchQuery('');
    setFilteredStaff(DUMMY_STAFF);
  };

  const filterOptions = ['코치', '매니저', '대표', '운영팀', '코칭팀', '활성', '비활성'];

  return (
    <CMSLayout currentPath="/cms/staff/search">
      <PageContainer>
        <SearchSection>
          <SearchRow>
            <SearchInput
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="직원명, ID, 전화번호, 이메일, 직급, 부서로 검색..."
            />
            <Button onClick={handleSearch}>검색</Button>
            <Button variant="secondary" onClick={() => setShowFilters(!showFilters)}>
              필터
            </Button>
            {(activeFilters.length > 0 || searchQuery) && (
              <Button variant="secondary" onClick={clearFilters}>
                초기화
              </Button>
            )}
          </SearchRow>

          <FilterRow $visible={showFilters}>
            <FilterLabel>필터:</FilterLabel>
            {filterOptions.map(filter => (
              <FilterChip
                key={filter}
                $active={activeFilters.includes(filter)}
                onClick={() => toggleFilter(filter)}
              >
                {filter}
              </FilterChip>
            ))}
          </FilterRow>
        </SearchSection>

        <ResultsSection>
          <ResultsHeader>
            <ResultsTitle>검색 결과</ResultsTitle>
            <ResultsCount>총 {filteredStaff.length}명</ResultsCount>
          </ResultsHeader>

          {filteredStaff.length > 0 ? (
            <TableContainer>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHeaderCell>직원ID</TableHeaderCell>
                    <TableHeaderCell>이름</TableHeaderCell>
                    <TableHeaderCell>연락처</TableHeaderCell>
                    <TableHeaderCell>이메일</TableHeaderCell>
                    <TableHeaderCell>직급</TableHeaderCell>
                    <TableHeaderCell>부서</TableHeaderCell>
                    <TableHeaderCell>권한</TableHeaderCell>
                    <TableHeaderCell>입사일</TableHeaderCell>
                    <TableHeaderCell>상태</TableHeaderCell>
                    <TableHeaderCell>액션</TableHeaderCell>
                  </TableRow>
                </TableHeader>
                <tbody>
                  {filteredStaff.map(staff => (
                    <TableRow key={staff.id}>
                      <TableCell>{staff.id}</TableCell>
                      <TableCell>{staff.name}</TableCell>
                      <TableCell>{staff.phone}</TableCell>
                      <TableCell>{staff.email}</TableCell>
                      <TableCell>{staff.position}</TableCell>
                      <TableCell>{staff.department}</TableCell>
                      <TableCell>
                        {staff.permissions.slice(0, 2).map(permission => (
                          <Tag key={permission}>{permission}</Tag>
                        ))}
                        {staff.permissions.length > 2 && (
                          <Tag>+{staff.permissions.length - 2}</Tag>
                        )}
                      </TableCell>
                      <TableCell>{staff.startDate}</TableCell>
                      <TableCell>
                        <Tag style={{ 
                          backgroundColor: staff.status === 'active' ? '#e8f5e8' : '#ffebee',
                          color: staff.status === 'active' ? '#2e7d32' : '#c62828'
                        }}>
                          {staff.status === 'active' ? '활성' : '비활성'}
                        </Tag>
                      </TableCell>
                      <TableCell>
                        <ActionButton onClick={() => handleStaffAction(staff.id, '상세보기')}>
                          상세보기
                        </ActionButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </tbody>
              </Table>
            </TableContainer>
          ) : (
            <EmptyState>
              <p>검색 결과가 없습니다.</p>
              <p>다른 검색어를 시도해보세요.</p>
            </EmptyState>
          )}
        </ResultsSection>
      </PageContainer>
    </CMSLayout>
  );
};

export default StaffSearch;
