import React, { useState } from 'react';
import styled from 'styled-components';
import CustomDateInput from '../components/CustomDateInput';
import { AppColors } from '../styles/colors';

const TestContainer = styled.div`
  padding: 40px;
  max-width: 600px;
  margin: 0 auto;
  background: ${AppColors.surface};
  min-height: 100vh;
`;

const TestSection = styled.div`
  margin-bottom: 40px;
  padding: 24px;
  border: 1px solid ${AppColors.borderLight};
  border-radius: 12px;
`;

const TestTitle = styled.h3`
  margin: 0 0 20px;
  color: ${AppColors.onSurface};
  font-size: 18px;
  font-weight: 600;
`;

const TestDescription = styled.p`
  margin: 0 0 20px;
  color: ${AppColors.onInput2};
  font-size: 14px;
  line-height: 1.5;
`;

const ValueDisplay = styled.div`
  margin-top: 12px;
  padding: 12px;
  background: ${AppColors.background};
  border-radius: 8px;
  font-family: monospace;
  font-size: 14px;
  color: ${AppColors.onInput3};
`;

const DateInputTest: React.FC = () => {
  const [basicDate, setBasicDate] = useState('');
  const [minMaxDate, setMinMaxDate] = useState('');
  const [errorDate, setErrorDate] = useState('');
  const [disabledDate, setDisabledDate] = useState('2024-01-15');

  const today = new Date();
  const minDate = new Date(today.getFullYear(), today.getMonth(), 1).toISOString().split('T')[0];
  const maxDate = new Date(today.getFullYear(), today.getMonth() + 1, 0).toISOString().split('T')[0];

  return (
    <TestContainer>
      <h1>CustomDateInput 테스트</h1>
      <p>Safari와 다른 브라우저에서 일관된 달력 UI를 제공합니다.</p>

      <TestSection>
        <TestTitle>기본 달력</TestTitle>
        <TestDescription>
          기본적인 날짜 선택 기능입니다. 모든 브라우저에서 동일한 UI를 제공합니다.
        </TestDescription>
        <CustomDateInput
          value={basicDate}
          onChange={setBasicDate}
          placeholder="날짜를 선택하세요"
        />
        <ValueDisplay>선택된 값: {basicDate || '(없음)'}</ValueDisplay>
      </TestSection>

      <TestSection>
        <TestTitle>날짜 제한</TestTitle>
        <TestDescription>
          이번 달 범위 내에서만 날짜를 선택할 수 있습니다. (최소: {minDate}, 최대: {maxDate})
        </TestDescription>
        <CustomDateInput
          value={minMaxDate}
          onChange={setMinMaxDate}
          placeholder="이번 달 날짜 선택"
          min={minDate}
          max={maxDate}
        />
        <ValueDisplay>선택된 값: {minMaxDate || '(없음)'}</ValueDisplay>
      </TestSection>

      <TestSection>
        <TestTitle>에러 상태</TestTitle>
        <TestDescription>
          에러 상태의 달력입니다. 빨간 테두리로 표시됩니다.
        </TestDescription>
        <CustomDateInput
          value={errorDate}
          onChange={setErrorDate}
          placeholder="에러 상태"
          error={true}
        />
        <ValueDisplay>선택된 값: {errorDate || '(없음)'}</ValueDisplay>
      </TestSection>

      <TestSection>
        <TestTitle>비활성 상태</TestTitle>
        <TestDescription>
          비활성화된 달력입니다. 클릭할 수 없습니다.
        </TestDescription>
        <CustomDateInput
          value={disabledDate}
          onChange={setDisabledDate}
          placeholder="비활성 상태"
          disabled={true}
        />
        <ValueDisplay>선택된 값: {disabledDate || '(없음)'}</ValueDisplay>
      </TestSection>
    </TestContainer>
  );
};

export default DateInputTest;
